/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { Button } from '@mui/material';
import { useRef, useState } from 'react';
import Modal from 'react-modal/lib/components/Modal';

const mapButtonSytle = {
    color: 'white',
    boxShadow: "0 0 0",
    backgroundColor: "#2BAE66",
    padding: "0px",
    fontSize: '0.8em',
    width: '55px',
    minWidth: '55px',

    "&:hover": {
        boxShadow: "0 0 0",
        backgroundColor: "#2BAE66"
    }
};

function ChatMapInfo() {
    const [open, setOpen] = useState(false);
    const mapRef = useRef(null);

    const waitModalRender = (cb) => {
        if (mapRef.current === null)
            setTimeout(() => waitModalRender(cb), 0);
        else
            cb();
    }

    const clickHandler = (e) => {
        setOpen(true);
    }
    const App = () => {
        const aa=useRef()
        useEffect(() => {
            console.log(aa.current)
        }, [])
        return <div ref={aa}>hihi</div>
    }
    return (
        <>
            <Button sx={mapButtonSytle} onClick={clickHandler}>위치</Button>
            <Modal
                isOpen={open}
                onRequestClose={() => setOpen(false)}
                ariaHideApp={false}
            >
                <App />
            </Modal>
        </>
    )
}

export default ChatMapInfo
