/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import MyChat from './type/MyChat';
import OtherChat from './type/OtherChat';
import SystemChat from './type/SystemChat';


const viewStyle = css`
  flex-grow: 1;
  height: 100%;
  border: 1px solid #2BAE66;
  border-radius: 5px;
  overflow-y: scroll;

  &::-webkit-scrollbar {
    display: none;
  }
  -ms-overflow-style: none;
  scrollbar-width: none;

  display: flex;
  flex-direction: column;
`;

function ChatView({chatList, setChatList, chatStyleRef}) {
    return (
        <div css={viewStyle} ref={chatStyleRef}>
            {chatList.map((chat, i) => {
                if (chat.type === 'me')
                    return <MyChat css={css`font-size: 1.5em`} text={chat.text} time={chat.time} key={i} />
                else if (chat.type === 'other')
                    return <OtherChat  css={css`font-size: 1.5em`} text={chat.text} time={chat.time} key={i} />
                else if (chat.type === 'system')
                    return <SystemChat css={css`font-size: 1.5em`} text={chat.text} time={chat.time} key={i} />
                return null;
            })}
        </div>
    )
}

export default ChatView
