/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import EnterButton from './EnterButton';
import StartTime from './StartTime';
import Start2End from './Start2End';
import { getSocket } from '../../socket/socket'
import { useEffect } from 'react';

const roomStyle = css`
    display: flex;

    justify-content: space-between;
    align-items: stretch;

    margin: 2px;
    padding: 6px;
    width: 99%;

    border-radius: 5px;

    background-color: #2BAE66;
    color: #FCF6F5;

    box-shadow: 3px 3px 3px gray;

`;

function Room({ roomData }) {
    const clickHandler = (e) => {
        e.preventDefault();
        getSocket().emit('joinRoomReq', roomData.id);
        console.log(3)
    }

    useEffect(() => {
        getSocket().on('joinRoomRes', (res) => {
            if (res.ok) {
                console.log(1)
            } else{
                alert(res.reason)
            }
        })
    }, [])

    return (
        <div css={roomStyle}>
            <StartTime time={roomData.time} />
            <Start2End start={roomData.start} startLoc={roomData.startLoc} end={roomData.end} endLoc={roomData.endLoc} />
            <EnterButton onClick={clickHandler}/>
        </div>
    )
}

export default Room
