/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import { Button, TextField } from '@mui/material';
import ChatView from './ChatView';
import { getSocket } from '../../socket/socket'
import { useEffect, useRef, useState } from 'react';
import dayjs from 'dayjs';

const chatStyle = css`
  height: calc(100% - 8px);
  margin: 3px;

  display: flex;
  flex-direction: column;
`;

const textFieldStyle = {
  flexGrow: '1',
  '.MuiOutlinedInput-root input': {
    padding: "5px;",
    fontSize: '0.8em',
    height: '20px'
  },
  '.MuiOutlinedInput-root fieldset': {
    borderColor: 'green',
  },
  '.MuiOutlinedInput-root.Mui-focused fieldset': {
    borderColor: 'green',
  }
};

const sendButtonStyle = {
  boxShadow: "0 0 0",
  backgroundColor: "#2BAE66",
  padding: "0px",
  fontSize: '0.8em',
  width: '55px',
  minWidth: '55px',

  "&:hover": {
    boxShadow: "0 0 0",
    backgroundColor: "#2BAE66"
  }
};

const leaveButtonStyle = {
  boxShadow: "0 0 0",
  backgroundColor: "#ff5b5b",
  padding: "0px",
  fontSize: '0.8em',
  width: '55px',
  minWidth: '55px',

  "&:hover": {
    boxShadow: "0 0 0",
    backgroundColor: "#ff5b5b"
  }
};

function Chat({ isInRoom, setIsInRoom, isHost, setIsHost, roomId, setRoomId, chatList, setChatList, addMessage }) {
  const [message, setMessage] = useState('');
  const chatStyleRef = useRef();

  const refreshScroll = (ref) => {
    ref.current.scrollTop = scrollHeight;
  }

  const chatHandler = (e) => {
    e.preventDefault();
    if (!isInRoom)
      return;

    getSocket().emit('sendMessageReq', {message: message, time: dayjs().format('HH:mm:ss')});
  }

  const leaveHandler = (e) => {
    e.preventDefualt();
    getSocket().emit('deleteRoomReq');
  }

  useEffect(() => {
    getSocket().on('deleteRoomRes', (res) => {
      if (res.ok) {

      }
    })
    getSocket().on('sendMessageRes', (res) => {
      if (res.ok) {
        addMessage({type: 'me', text: res.message, time: dayjs('2020-01-01 ' + res.time)});
      } else {
        alert(res.reason)
      }
    })
    getSocket().on('receiveMessage', (res) => {
        addMessage({type: 'other', text: res.message, time: dayjs('2020-01-01 ' + res.time)})
    })
  }, [])

  return (
    <div css={chatStyle}>
      {isInRoom
      ? <div css={css`height: 25px; display: flex; flex-direction: row-reverse; justify-content: center; padding-bottom: 3px;`}>
          <Button variant="contained" sx={leaveButtonStyle} onClick={leaveHandler}>{isHost ? '방 삭제' : '방 퇴장'}</Button>
        </div>
      : <></>}
      <ChatView chatList={chatList} setChatList={setChatList} chatStyleRef={chatStyleRef}/>
      <form css={css`display:flex;height:30px;`} onSubmit={chatHandler}>
        <TextField sx={textFieldStyle} value={message} onChange={(e) => setMessage(e.target.value)} />
        <Button variant="contained" sx={sendButtonStyle} onClick={chatHandler}>보내기</Button>
      </form>
    </div>
  )
}

export default Chat;
